drop table if exists Missions CASCADE;
drop table if exists To_Do CASCADE;
create table Missions (id bigint generated by default as identity, responsibility varchar(20), completedstatus  boolean, nameofMission varchar(20), orderofimportance integer, my_to_do_id bigint, primary key (id));
create table To_Do (id bigint generated by default as identity, list_nameofMission varchar(20), primary key (id));